INSERT INTO `wp_postmeta` VALUES (5061, 4627, 'panels_data', 'a:3:{s:7:\"widgets\";a:3:{i:0;a:5:{s:5:\"title\";s:40:\"ĐÔI ĐỀU VỀ GIÀY VULCANISED - COX\";s:4:\"text\";s:1613:\"<div class=\"shortcode-title \">\n<h1 class=\"fancy-type small-title\"></h1>\n</div>\n<div class=\"static-text-container left\">\n<div class=\"text\">\n\nGiày Vulcanised được gọi bằng Tiếng Việt là Giày Lưu Hóa được sản xuất theo qui trình rất chặt chẽ bởi từng khâu quản lý chuyên nghiệp. Hơn 2000 công nhân với kinh nghiệm làm Giày lâu năm luôn đem đến người tiêu dùng bằng những đôi Giày cực kỳ chắc chắn, mềm mại với những đường kim mủi chỉ may chuẩn theo từng mm và luôn đạt chuẩn Quốc Tế.\n\nHàng năm đội ngũ công nhân viên của nhà máy sản xuất với số lượng hàng xuất khẩu ra thị trường Quốc Tế hơn 2 triệu đôi Giày của các thương <span class=\"text_exposed_show\">hiệu nổi tiếng trên Thế Giới.</span>\n<div class=\"text_exposed_show\">\n\n<a class=\"_58cn\" href=\"https://www.facebook.com/hashtag/cox?source=feed_text&amp;story_id=1322701274411849\" data-ft=\"{&quot;tn&quot;:&quot;*N&quot;,&quot;type&quot;:104}\"><span class=\"_58cl\">‪#‎</span><span class=\"_58cm\">COX‬</span></a> muốn truyền tải những hình ảnh sau đây để nói lên đội ngũ công nhân VIỆT NAM chúng ta luôn chăm chỉ và luôn làm ra những sản phẩm mang tầm cỡ Quốc Tế. Giờ đây #COX shoes đã và đang làm ra những mẫu Giày Vulcanise thời trang trẻ trung đầy cá tính và năng động để NGƯỜI VIỆT NAM CHÚNG TA mang những đôi “Giày Chuẩn Quốc Tế và Giá cực kỳ Ưu Việt”.\n\n</div>\n</div>\n</div>\";s:6:\"filter\";b:1;s:6:\"visual\";b:1;s:11:\"panels_info\";a:6:{s:5:\"class\";s:14:\"WP_Widget_Text\";s:4:\"grid\";i:0;s:4:\"cell\";i:0;s:2:\"id\";i:0;s:9:\"widget_id\";s:36:\"989f76b5-6c10-4ff2-b524-5f3940777286\";s:5:\"style\";a:2:{s:27:\"background_image_attachment\";b:0;s:18:\"background_display\";s:4:\"tile\";}}}i:1;a:5:{s:4:\"type\";s:6:\"visual\";s:5:\"title\";s:34:\"Các dịch vụ của chúng tôi\";s:4:\"text\";s:1345:\"<p>[container classname=\"aboutu